sp_widget.client_script:
function($stateParams, $log, $scope, $http, $mdDialog, etrackTransferAsset, $state) {
	/* widget controller */
	var c = this;

	$scope.trasnferComplete = function(){
		var confirm = $mdDialog.confirm()
		.title('Are you sure you complete the trasnfer?')
		.ok('YES')
		.cancel('NO');
		$mdDialog.show(confirm).then(function() {
			etrackTransferAsset.completeTrasnfer(c.data.transferAsset).then(function(res){
				$mdDialog.show(
					$mdDialog.alert()
					.parent(angular.element(document.querySelector('#popupContainer')))
					.clickOutsideToClose(true)
					.title("Asset has been trasnfered successfully!")
					.ok('OK')
				);
				$state.go("assets");
				$log.log(res);
			}, function(err){
				$log.log(err);
				alert("ERROR");
			});
		}, function() {
		});
	}
	$scope.doTheBack = function() {
		$state.go("assets");
	};
}

sp_widget.script:
(function() {

	data.id = "";
	data.transferAsset = {};
	var mysys_name = gs.getUserDisplayName();
	var mysys_id = gs.getUserID();
	data.info = {checkOutUser: mysys_name};
	data.admin = gs.getUser().isMemberOf("ETracking_Admin")



	if (input && input.id) {
		data.id = input.id.toString();
		var transferAsset = new GlideRecord('x_naci_e_tracking_assdets_transfer');
		transferAsset.get(data.id)
		data.transferAsset.id = transferAsset.sys_id.toString();
		data.transferAsset.owner = transferAsset.asset.owner.name.toString();
		data.transferAsset.from = transferAsset.from.name.toString();
		data.transferAsset.to = transferAsset.to.name.toString();
		data.transferAsset.quantity = transferAsset.quantity.toString();
		data.transferAsset.user_id = mysys_id;
	}




})();
