sp_widget.client_script:
function($stateParams, $log, $scope, $http, $mdDialog, etrackUpdateAsset) {
  /* widget controller */
  var c = this;

	$("#ownerName").autocomplete({
		delay: 300,
		source: function(request, response) {
			$('.ui-autocomplete').css('display', 'block');
			var term = $('#ownerName').val();
			response([]);
			if (term.length < 4) {
				return;
			}
			var temp = '';
			if (term.indexOf(',') >= 0) {
				temp = term.split(',');
				var length = temp.length;
				if (length > 1) {
					term = temp[1].trim() + " " + temp[0].trim();
				} else {
					term = temp[0];
				}
			}
			var req = {
				method: 'GET',
				url: '/api/x_naci_account_act/accountactivationscoped/fetchUserList',
				beforeSend: function(xhr) {
					xhr.setRequestHeader('X-UserToken', window.g_ck);
				},
				headers: {
					'Content-Type': 'application/json'
				},
				params: {
					'term': term
				}
			};
			$http(req).then(handleResponse, handleError);

			function handleResponse(responseData) {
				var result = responseData.data.result;
				response(result);
			}

			function handleError(response) {
				console.error(response);
				alert('ERROR');
			}
		},
		autoFocus: true,
		select: function(event, ui) {


			var sys_id = ui.item.id;
			var name = ui.item.value;

			if (sys_id != null && sys_id.trim().length > 1) {

				c.data.asset.owner = name;
				c.data.asset.ownerId = sys_id;
			} else {
				c.data.asset.owner = "";
				c.data.asset.ownerId = "";
			}

			//$('#content').css('height', '550px');
			$scope.$apply();
		}
	});


	$scope.enableEdit = false;

	$scope.updateAsset = function(){
		etrackUpdateAsset.updateAsset(c.data.asset).then(function(res){
			$mdDialog.show(
				$mdDialog.alert()
				.parent(angular.element(document.querySelector('#popupContainer')))
				.clickOutsideToClose(true)
				.title("Asset has been updated successfully!")
				.ok('OK')
			);

			$log.log(res);
		}, function(err){
			$log.log(err);
			alert("ERROR");
		});

	};



}

sp_widget.script:
(function() {

	data.id = "";
	data.asset = {};
	data.model = {};
	data.stockrooms = [];



	if (input && input.id) {
		data.id = input.id.toString();
		var asset = new GlideRecord('x_naci_e_tracking_etrack_assets');
		asset.addQuery("sys_id", data.id);
		asset.query();
		asset.next();
		var item = {};
		data.asset.id = asset.sys_id.toString();
		data.asset.barcode = asset.barcode.toString();
		data.asset.owner = asset.owner.name.toString();
		data.asset.quantity = asset.quantity.toString();
		data.asset.stockroom = asset.stockroom.sys_id.toString();
		data.model.name = asset.model.name.toString();
		data.model.manufacturer = asset.model.manufacture.name.toString();
		data.model.description = asset.model.description.toString();
		data.model.model_number= asset.model.model_number.toString();
		data.model.unit_cost = asset.model.cost.toString();
	}




	var stockroom = new GlideRecord('x_naci_e_tracking_stock_room');
	stockroom.orderBy("name");
	stockroom.query();
	while(stockroom.next()){
		item = {};
		item.id = stockroom.sys_id.toString();
		item.name = stockroom.name.toString();
		data.stockrooms.push(item);
	}




})();
